template.value:
//classpath "org.jfrog.buildinfo:build-info-extractor-gradle:4.8.1"
//apply plugin: 'com.jfrog.artifactory'
//apply plugin: 'maven-publish'

def commonGroupId = '$groupId$'
def currentVersion = '$currentVersion$'

project('$projectName$') {
    artifactoryPublish.dependsOn('build')
    publishing {
        publications {
            aar(MavenPublication) {
                groupId = commonGroupId
                artifactId = project.getName()
                version = currentVersion
                // Tell maven to prepare the generated "*.aar" file for publishing
                artifact("$buildDir/outputs/aar/${project.getName()}-release.aar")
                pom.withXml {
                    def dependenciesNode = asNode().appendNode('dependencies')
                    configurations.api.allDependencies.each {
                        def dependencyNode = dependenciesNode.appendNode('dependency')
                        dependencyNode.appendNode('groupId', it.group)
                        dependencyNode.appendNode('artifactId', it.name)
                        dependencyNode.appendNode('version', it.version)
                    }
                }
            }
        }
    }

    artifactoryPublish {
        publications(publishing.publications.aar)
    }
}


/**
 * These is the section for common jFrog oss configuration
 *
 */
artifactory {
//    clientConfig.setIncludeEnvVars(true)
//    clientConfig.info.addEnvironmentProperty('test.adding.dynVar',new Date().toString())
//    clientConfig.timeout = 600
    contextUrl = '$repoUrl$'
    publish {
        repository {
            repoKey = '$repoKey$' // The Artifactory repository key to publish to
            username = artifactory_username // The publisher user name
            password = artifactory_password // The publisher password
            // This is an optional section for configuring Ivy publication (when publishIvy = true).
            ivy {
                ivyLayout = '[organization]/[module]/ivy-[revision].xml'
                artifactLayout = '[organization]/[module]/[revision]/[module]-[revision](-[classifier]).[ext]'
                mavenCompatible = true //Convert any dots in an [organization] layout value to path separators, similar to Maven's groupId-to-path conversion. True if not specified
            }
        }
        defaults {
            // Reference to Gradle publications defined in the build script.
            // This is how we tell the Artifactory Plugin which artifacts should be
            // published to Artifactory.
            publishArtifacts = true
            publishBuildInfo = false
            publishIvy = false
            // Properties to be attached to the published artifacts.
            properties = ['qa.level': 'basic', 'dev.team' : 'core']
            publishPom = true // Publish generated POM files to Artifactory (true by default)
        }
    }
}
